import delay from './delay';

class HttpClient {
  constructor(baseURL) {
    this.baseURL = baseURL;
  }

  async get(path) {
    await delay(500);

    const response = await fetch(`${this.baseURL}${path}`);

    if (response.ok) {
      response.json();
    }

    throw new Error(`${response.status} - ${response.statusText}`);
  }
}
export default HttpClient;
