import delay from '../utils/delay';

class ContactService {
  async listContacts(orderBy = 'asc') {
    const resp = fetch(`http://localhost:3001/contacts?orderBy=${orderBy}`);

    await delay(500);

    return resp.json();
  }
}

export default new ContactService();
