import delay from './delay';

class HttpClient {
  constructor(baseURL) {
    this.baseURL = baseURL;
  }

  async get(path) {
    const response = await fetch(`${this.baseURL}${path}`);

if(response.ok)

    await delay(500); // tirar isso quando for fazer a biuld do projeto

    return response.json();
  }
}
export default HttpClient;
