import styled from 'styled-components';

export const Container = styled.header`
  a {
    text-decoration: none;
    span {
      color: ${({ theme }) => theme.colors.primary.main};
      font-weight: bold;
    }
  }

  img {
    height: 20px;
    width: 16px;
    margin-right: 8px;
    transform: rotate(-90deg);
  }

  h1 {
    font-size
  }
`;
